export class DocumentaryCollection extends BasicStreamableCollection<Documentary> {

    /**
     * __Constructor de la clase DocumentaryCollection__
     * @param collection -- Colecci√≥n de documentales
     */
    constructor (collection: Documentary[]) {
        super(collection);
    }

    /**
     * Implementaci√≥n del m√©todo para buscar un elemento por su a√±o de estreno
     * @param year -- A√±o de estreno
     * @returns -- Colecci√≥n de documentales que coincidan con el a√±o de estreno pasado por par√°metro
     */
    findByYear(year: number): Documentary[] {
        return this.collection.filter((doc) => doc.getYear() === year);
    }

    /**
     * M√©todo extendido de la clase BasicStreamableCollection para saber si un documental existe en nuestra colleci√≥n de documentales
     * @param title -- T√≠tulo del documental
     * @returns -- Booleano que indica si el documental existe o no en nuestra colecci√≥n
     */
    findByTitle(title: string): boolean {
        let found = false;
        this.collection.forEach((doc) => {
            if (doc.getTitle() === title) {
                found = true;
            }
        });
        if (found) {
            return true;
        }
        return false;
    }

    /**
     * M√©todo extendido de la clase BasicStreamableCollection para buscar un elemento por su g√©nero 
     * @param gender -- G√©nero del documental
     * @returns -- Colecci√≥n de documentales que coincidan con el g√©nero pasado por par√°metro
     */
    findByGender(gender: string): Documentary[] {
        return this.collection.filter((doc) => doc.getGender() === gender);
    }

    /**
     * M√©todo para obtener la colecci√≥n de documentales
     * @returns -- Colecci√≥n de documentales
     */
    public getCollection(): Documentary[] {
        return this.collection;
    }

    /**
     * Pasado un t√≠tulo de un documental como parametro de la funci√≥n, 
     * devuelve la duraci√≥n del mismo buscando en la colecci√≥n el documental con dicho t√≠tulo.
     * @param title -- T√≠tulo del documental
     * @returns -- Duraci√≥n del documental
     */
    public getTimeOfFilm(title: string): number {
        let time = 0;
        this.collection.forEach((doc) => {
            if (doc.getTitle() === title) {
                time = doc.getDuration();
            }
        });
        return time;
    }
}

